}//                                  |}
//                                   |f1();

f1();//undefined,===================>|var num;
var num=20;//                        |function f1(){
function f1(){//                     |    console.log(num); 
    console.log(num);//              |}
}//                                  |f1(); 
//                                   |num=20;
