var falg=reg.test(str);//返回true或false
reg.exec(str);//与下面的match类似，不过调用一次只能输出一个.若reg里添加g，就是全局;要多次输出需多次调用console.log(reg.exec(str));
str.match(reg);//返回str里面符合reg条件的字符串,只匹配一次
