app.use(session({
    // 配置加密字符串，它会在原有加密基础之上和这个字符串拼接起来去加密
    // 目的：为了增加安全性，防止客户端恶意伪造
    secret: 'keyboard cat',
    resave: false,
    saveUninitialized: true // 无论是否使用 Session ，默认直接分配一把钥匙给客户端
}))

