gs.append("rect")
    .attr("x",function(d,i){
    return xScale(i)+rectPadding/2;
})    
    .attr("y",function(d){
    return yScale(d);
})
    .attr("width",function(){
    return xScale.step()-rectPadding;
})
    .attr("height",function(d){
